object q3_length_of_longest_substring {

  def lengthOfLongestSubstringMy(s: String): Int = {
    val list: ListBuffer[String] = ListBuffer()

    if (s.length == 1) {
      return 1
    }

    var c = s
    for (_ <- 0 until s.length - 1) {
      if (lookup(c, list)) {
        return getLongestLength(list)
      }

      c = c.tail
    }

    getLongestLength(list)
  }

  def getLongestLength(list: ListBuffer[String]): Int = {
    if (list.isEmpty) 0 else {
      val sorted = list.sortWith((a, b) => a.length > b.length)
      for (c <- sorted.head) {
        print(c)
      }
      println()
      sorted.head.length
    }
  }

  def lookup(s: String, list: ListBuffer[String]): Boolean = {
    var tmp: String = ""
    for (i <- 0 until s.length) {
      val char = s.charAt(i)
      if (tmp.contains(char)) {
        list += tmp
        val e = s.replace(tmp, "")
        if (tmp.contains(e)) {
          return true
        }
        tmp = ""
      }
      tmp += char
    }
    if (tmp.nonEmpty) {
      list += tmp
    }
    false
  }

  def lengthOfLongestSubstringOfficial(s: String): Int = {
    if (s.isEmpty) return 0
    val mem: mutable.HashMap[Char, Int] = mutable.HashMap()
    var start: Int = 0
    var maxLength = 0
    for (i <- 0 until s.length) {
      val c: Char = s.charAt(i)
      if (mem.contains(c)) {
        val n = mem(c) + 1
        start = if (start > n) start else n
      }
      val m = i - start + 1
      maxLength = if (maxLength > m) maxLength else m
      mem.put(c, i)
    }
    maxLength
  }

